import React, { useState } from "react";

const Clock = () => {
  const [waktu, setwaktu] = useState(21 * 60);
  const [breaktime, setbreaktime] = useState(15 * 60);

  const formatwaktu = (time) => {
    let minutes = Math.floor(time / 60);
    let detik = time % 60;

    return (
      (minutes < 10 ? "0" + minutes : minutes) +
      ":" +
      (detik < 10 ? "0" + detik : detik)
    );
  };

  return (
    <div>
      <Length breaktime={breaktime} />
      <div className="container">{formatwaktu(waktu)}</div>
    </div>
  );
};

function Length({ title, changeTime, type, breaktime, formatwaktu }) {
  return (
    <div>
      <div>
        <h1>Break Length</h1>
        <div>
          <button>tambah</button>
          <h3>{formatwaktu(breaktime)}</h3>
        </div>
      </div>
    </div>
  );
}

export default Clock;
