import React, { useState } from "react";

const Clock = () => {
  const [waktu, setwaktu] = useState(21 * 60);
  const [breaktime, setbreaktime] = useState(11 * 60);
  const [session, setsession] = useState(11 * 60);
  const [timeon, settimeon] = useState(false);

  const formatwaktu = (time) => {
    let minutes = Math.floor(time / 60);
    let detik = time % 60;

    return (
      (minutes < 10 ? "0" + minutes : minutes) +
      ":" +
      (detik < 10 ? "0" + detik : detik)
    );
  };

  const changeTime = (amount, type) => {
    if (type === "break") {
      if (breaktime <= 60 && amount < 0) {
        return;
      }

      setbreaktime((prev) => prev + amount);
    } else {
      if (session <= 60 && amount < 0) {
        return;
      }
      setsession((prev) => prev + amount);
      if (!timeon) {
        setwaktu(setsession + amount);
      }
    }
  };

  return (
    <div>
      <Length
        changeTime={changeTime}
        title="Break Length"
        formatwaktu={formatwaktu}
        type={"break"}
        breaktime={breaktime}
      />
      <Length
        changeTime={changeTime}
        title="Session Length"
        formatwaktu={formatwaktu}
        type={"session"}
        session={session}
      />
      <div className="container">{formatwaktu(waktu)}</div>
    </div>
  );
};

function Length({ title, type, changeTime, breaktime, formatwaktu }) {
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <div>
        <h1>{title}</h1>
        <div style={{ display: "flex", gap: "10px" }}>
          <button onClick={() => changeTime(60, type)}>tambah</button>
          <h3>{formatwaktu(breaktime)}</h3>
          <button onClick={() => changeTime(-60, type)}>kurang</button>
        </div>
      </div>
      <div>
        <h1>{title}</h1>
        <div style={{ display: "flex", gap: "10px" }}>
          <button onClick={() => changeTime(60, type)}>tambah</button>
          <h3>{formatwaktu(breaktime)}</h3>
          <button onClick={() => changeTime(-60, type)}>kurang</button>
        </div>
      </div>
    </div>
  );
}

export default Clock;
